import org.apache.commons.collections4.map.HashedMap;
import java.util.HashMap;
import java.util.ArrayList;

public class Main {
    public static void main(String[] args) {
        HashedMap<String, Integer> map = new HashedMap<>();
        HashMap<String, String> stringMap = new HashMap<>();
        ArrayList<Integer> numbers = new ArrayList<>();

        // Adding some unrelated operations
        stringMap.put("greeting", "Hello, World!");
        numbers.add(10);
        numbers.add(20);
        numbers.add(30);

        /* write */ map.put("key", 42);

        // Some complex operations
        int sum = 0;
        for (int number : numbers) {
            sum += number;
        }
        System.out.println("Sum of numbers: " + sum);

        String greetingMessage = stringMap.get("greeting");
        System.out.println(greetingMessage);

        // More unrelated operations
        int max = Integer.MIN_VALUE;
        for (int number : numbers) {
            if (number > max) {
                max = number;
            }
        }
        System.out.println("Maximum number: " + max);

        /* read */ Integer value = map.get("key");
        System.out.println("Value from map: " + value);

        // Further operations to make the program complex
        for (int i = 0; i < numbers.size(); i++) {
            numbers.set(i, numbers.get(i) * 2);
        }

        for (int number : numbers) {
            System.out.println("Doubled number: " + number);
        }
    }
}
