import org.apache.commons.collections4.multimap.HashSetValuedHashMap;
import java.util.Collection;
import java.util.HashMap;
import java.util.Map;
import java.util.Set;

public class Main {
    public static void main(String[] args) {
        // Original HashSetValuedHashMap instance
        HashSetValuedHashMap<String, Integer> map = new HashSetValuedHashMap<>();

        // Another HashSetValuedHashMap instance for demonstration
        HashSetValuedHashMap<String, String> map2 = new HashSetValuedHashMap<>();

        // Regular HashMap for storing additional data
        Map<String, Integer> regularMap = new HashMap<>();

        // Populate map2 with some values
        map2.put("name", "Alice");
        map2.put("name", "Bob");

        // Populate regularMap with some values
        regularMap.put("age", 30);
        regularMap.put("height", 175);

        // Use the original map instance
        /* write */ map.put("key", 42);

        // Perform operations on map2
        Set<String> keys = map2.keySet();
        for (String key : keys) {
            Collection<String> names = map2.get(key);
            for (String name : names) {
                System.out.println("Name: " + name);
            }
        }

        // Perform operations on regularMap
        for (Map.Entry<String, Integer> entry : regularMap.entrySet()) {
            System.out.println(entry.getKey() + ": " + entry.getValue());
        }

        // Use the original map instance again
        /* read */ Collection<Integer> values = map.get("key");
        for (Integer value : values) {
            System.out.println(value);
        }
    }
}
