        this.name = name;
        this.age = age;
    }

    public void addScore(int score) {
        scores.addScore(score);
    }

    public int getScore(int index) {
        return scores.getScore(index);
    }

    public String getName() {
        return name;
    }

    public int getAge() {
        return age;
    }
}

