import org.apache.commons.collections4.map.ReferenceMap;
import java.util.HashMap;
import java.util.Map;

public class Main {
    public static void main(String[] args) {
        ReferenceMap<String, Integer> map = new ReferenceMap<>();
        Map<String, String> hashMap = new HashMap<>();

        /* write */ map.put("key", 42);
        hashMap.put("alpha", "A");
        hashMap.put("beta", "B");

        // Perform some unrelated operations
        for (Map.Entry<String, String> entry : hashMap.entrySet()) {
            System.out.println("HashMap Key: " + entry.getKey() + ", Value: " + entry.getValue());
        }

        // More operations on the ReferenceMap
        map.put("anotherKey", 100);
        Integer anotherValue = map.get("anotherKey");
        System.out.println("Another Value: " + anotherValue);

        // Do some calculations
        int sum = 0;
        for (int i = 0; i < 10; i++) {
            sum += i;
        }
        System.out.println("Sum: " + sum);

        /* read */ Integer value = map.get("key");
        System.out.println("Original Value: " + value);

        // Further operations
        map.put("thirdKey", 300);
        Integer thirdValue = map.get("thirdKey");
        System.out.println("Third Value: " + thirdValue);
    }
}
