import org.apache.commons.collections4.multimap.TreeValuedHashMap;
import org.apache.commons.collections4.multimap.ArrayListValuedHashMap;
import java.util.Set;
import java.util.HashMap;

public class Main {
    public static void main(String[] args) {
        TreeValuedHashMap<String, Integer> map = new TreeValuedHashMap<>();
        ArrayListValuedHashMap<String, String> anotherMap = new ArrayListValuedHashMap<>();
        HashMap<String, Double> simpleMap = new HashMap<>();

        // Insert data into anotherMap
        anotherMap.put("fruit", "apple");
        anotherMap.put("fruit", "banana");
        anotherMap.put("vegetable", "carrot");

        // Insert data into simpleMap
        simpleMap.put("pi", 3.14159);
        simpleMap.put("e", 2.71828);

        // Perform operations on anotherMap
        Set<String> keys = anotherMap.keySet();
        for (String key : keys) {
            System.out.println("Key: " + key + ", Values: " + anotherMap.get(key));
        }

        // Perform operations on simpleMap
        for (String key : simpleMap.keySet()) {
            System.out.println("Key: " + key + ", Value: " + simpleMap.get(key));
        }

        /* write */ map.put("key", 100);

        // Perform some unrelated computations
        int sum = 0;
        for (int i = 0; i < 10; i++) {
            sum += i;
        }
        System.out.println("Sum of first 10 natural numbers: " + sum);

        // More unrelated operations
        String[] animals = {"cat", "dog", "bird"};
        for (String animal : animals) {
            System.out.println("Animal: " + animal);
        }

        /* read */ Integer value = map.get("key").first();
        System.out.println("Value from TreeValuedHashMap: " + value);

        // Further computations
        double total = 0.0;
        for (double val : simpleMap.values()) {
            total += val;
        }
        System.out.println("Total of simpleMap values: " + total);
    }
}
