import org.apache.commons.collections4.MultiValuedMap;
import org.apache.commons.collections4.multimap.TransformedMultiValuedMap;
import org.apache.commons.collections4.map.MultiValueMap;
import java.util.HashMap;
import java.util.Map;

public class Main {
    public static void main(String[] args) {
        // Original map setup
        MultiValuedMap<String, String> originalMap = new MultiValueMap<>();
        TransformedMultiValuedMap<String, String> transformedMap = new TransformedMultiValuedMap<>(
                originalMap, null, null);

        // Additional map setup
        Map<String, Integer> simpleMap = new HashMap<>();
        simpleMap.put("a", 1);
        simpleMap.put("b", 2);

        // Operations on additional map
        for (Map.Entry<String, Integer> entry : simpleMap.entrySet()) {
            System.out.println("Key: " + entry.getKey() + ", Value: " + entry.getValue());
        }

        // Write operation on TransformedMultiValuedMap
        /* write */ transformedMap.put("key", "value");

        // Additional operations on transformedMap
        transformedMap.put("anotherKey", "anotherValue");
        transformedMap.put("yetAnotherKey", "yetAnotherValue");

        // Perform operations based on transformedMap
        for (String key : transformedMap.keySet()) {
            System.out.println("TransformedMap Key: " + key);
            for (String value : transformedMap.get(key)) {
                System.out.println("TransformedMap Value: " + value);
            }
        }

        // Read operation on TransformedMultiValuedMap
        /* read */ String value = transformedMap.get("key").iterator().next();
        System.out.println(value);

        // Further operations
        simpleMap.put("c", 3);
        int sum = 0;
        for (Integer val : simpleMap.values()) {
            sum += val;
        }
        System.out.println("Sum of values in simpleMap: " + sum);
    }
}
