import org.apache.commons.compress.archivers.jar.JarArchiveEntry;
import java.util.ArrayList;

public class Main {
    public static void main(String[] args) {
        JarArchiveEntry entry = new JarArchiveEntry("example.jar");
        ArrayList<Integer> numberList = new ArrayList<>();
        ArrayList<String> commentList = new ArrayList<>();

        // Adding numbers to the numberList
        for (int i = 0; i < 10; i++) {
            numberList.add(i * 2);
        }

        // Adding comments to the commentList
        commentList.add("First comment");
        commentList.add("Second comment");

        /* write */ entry.setComment("This is a test comment");

        // Processing numberList
        int sum = 0;
        for (int num : numberList) {
            sum += num;
        }
        System.out.println("Sum of numbers: " + sum);

        // Processing commentList
        for (String comment : commentList) {
            System.out.println(comment);
        }

        /* read */ String comment = entry.getComment();
        System.out.println("Jar Comment: " + comment);

        // Adding more complex logic
        int maxNumber = numberList.get(0);
        for (int num : numberList) {
            if (num > maxNumber) {
                maxNumber = num;
            }
        }
        System.out.println("Max number: " + maxNumber);

        // Reverse the commentList
        ArrayList<String> reversedComments = new ArrayList<>();
        for (int i = commentList.size() - 1; i >= 0; i--) {
            reversedComments.add(commentList.get(i));
        }
        System.out.println("Reversed comments:");
        for (String reversedComment : reversedComments) {
            System.out.println(reversedComment);
        }
    }
}
