import org.apache.commons.compress.archivers.tar.TarArchiveEntry;
import java.util.ArrayList;
import java.util.HashMap;

public class Main {
    public static void main(String[] args) {
        TarArchiveEntry entry = new TarArchiveEntry("example.txt");
        ArrayList<TarArchiveEntry> entryList = new ArrayList<>();
        HashMap<String, Integer> typeMap = new HashMap<>();

        // Adding entries to the list
        entryList.add(entry);
        TarArchiveEntry anotherEntry = new TarArchiveEntry("another_example.txt");
        entryList.add(anotherEntry);

        // Setting file types for entries
        /* write */ entry.setFileType(TarArchiveEntry.FILE_TYPE_NORMAL);
        anotherEntry.setFileType(TarArchiveEntry.FILE_TYPE_DIRECTORY);

        // Storing file types in a map with their names
        for (TarArchiveEntry e : entryList) {
            typeMap.put(e.getName(), e.getFileType());
        }

        // Performing some operations on the map
        for (String name : typeMap.keySet()) {
            System.out.println("Entry: " + name + ", Type: " + typeMap.get(name));
        }

        // Some additional logic using the map
        if (typeMap.containsValue(TarArchiveEntry.FILE_TYPE_NORMAL)) {
            System.out.println("Normal file type found.");
        }

        // Retrieve the file type of the original entry
        /* read */ int fileType = entry.getFileType();
        System.out.println("Original entry file type: " + fileType);

        // More complex operations
        int sumOfTypes = 0;
        for (int type : typeMap.values()) {
            sumOfTypes += type;
        }
        System.out.println("Sum of all file types: " + sumOfTypes);

        // Final operations
        if (sumOfTypes > 0) {
            System.out.println("There are entries with valid file types.");
        }
    }
}
