import org.apache.commons.compress.compressors.gzip.GzipParameters;
import java.util.ArrayList;

public class Main {
    public static void main(String[] args) {
        GzipParameters gzipParameters = new GzipParameters();
        ArrayList<String> stringList = new ArrayList<>();
        ArrayList<Integer> integerList = new ArrayList<>();

        // Adding elements to the string list
        stringList.add("Hello");
        stringList.add("World");

        // Adding elements to the integer list
        for (int i = 0; i < 5; i++) {
            integerList.add(i * 10);
        }

        // Performing operations on the integer list
        int sum = 0;
        for (Integer num : integerList) {
            sum += num;
        }

        // Set the header CRC
        /* write */ gzipParameters.setHeaderCRC(true);

        // Perform some operations on string list
        StringBuilder combinedString = new StringBuilder();
        for (String str : stringList) {
            combinedString.append(str).append(" ");
        }

        // Print the combined string
        System.out.println("Combined String: " + combinedString.toString().trim());

        // Perform a complex operation with the sum
        int product = sum * 2;
        System.out.println("Product of sum and 2: " + product);

        // Get the header CRC
        /* read */ boolean headerCRC = gzipParameters.getHeaderCRC();
        System.out.println("Header CRC: " + headerCRC);

        // Further operations
        ArrayList<Double> doubleList = new ArrayList<>();
        for (int i = 0; i < 3; i++) {
            doubleList.add(i * 1.5);
        }

        // Print all elements in double list
        for (Double d : doubleList) {
            System.out.println("Double value: " + d);
        }
    }
}
