import org.apache.commons.compress.archivers.jar.JarArchiveEntry;
import java.util.HashMap;
import java.util.Map;

public class Main {
    public static void main(String[] args) {
        JarArchiveEntry jarEntry = new JarArchiveEntry("initialName");
        Map<String, Integer> map = new HashMap<>();

        map.put("key1", 100);
        map.put("key2", 200);

        /* write */ jarEntry.setName("newName");

        int sum = 0;
        for (String key : map.keySet()) {
            sum += map.get(key);
        }

        if (sum > 250) {
            map.put("key3", 300);
        } else {
            map.put("key3", 150);
        }

        System.out.println("Sum of values: " + sum);

        for (Map.Entry<String, Integer> entry : map.entrySet()) {
            System.out.println(entry.getKey() + ": " + entry.getValue());
        }

        /* read */ String name = jarEntry.getName();
        System.out.println("Jar entry name: " + name);

        int product = 1;
        for (int value : map.values()) {
            product *= value;
        }

        System.out.println("Product of values: " + product);
    }
}
