import com.google.common.collect.LinkedHashBiMap;
import com.google.common.collect.HashBiMap;
import java.util.ArrayList;

public class Main {
    public static void main(String[] args) {
        LinkedHashBiMap<String, Integer> biMap = LinkedHashBiMap.create();
        HashBiMap<String, String> hashBiMap = HashBiMap.create();
        ArrayList<Integer> list = new ArrayList<>();

        // Using HashBiMap to store some values
        hashBiMap.put("A", "Apple");
        hashBiMap.put("B", "Banana");

        // Adding values to ArrayList
        for (int i = 1; i <= 5; i++) {
            list.add(i * 10);
        }

        // Processing ArrayList
        for (Integer num : list) {
            System.out.println("Processing number: " + num);
        }

        /* write */ biMap.put("key", 42);

        // Additional operations with HashBiMap
        String fruit = hashBiMap.get("A");
        System.out.println("Fruit corresponding to 'A': " + fruit);

        // More operations with ArrayList
        list.add(100);
        list.add(200);

        // Print all elements in the list
        for (Integer num : list) {
            System.out.println("List item: " + num);
        }

        /* read */ Integer value = biMap.get("key");
        System.out.println("Value retrieved from biMap: " + value);

        // Final operations
        list.remove(0);
        System.out.println("First item removed from list, new first item: " + list.get(0));
    }
}
