        this.name = name;
        this.age = age;
    }

    public void addScore(int score) {
        scores.addScore(score);
    }

    public String getName() {
        return name;
    }

    public int getAge() {
        return age;
    }

    public StudentScores getScores() {
        return scores;
    }
}

