import com.google.common.collect.LinkedListMultimap;
import java.util.HashMap;

public class Main {
    public static void main(String[] args) {
        LinkedListMultimap<String, Integer> multimap = LinkedListMultimap.create();
        HashMap<String, String> hashmap = new HashMap<>();
        hashmap.put("name", "John");
        hashmap.put("age", "30");

        // Performing operations on the hashmap
        for (String key : hashmap.keySet()) {
            System.out.println(key + ": " + hashmap.get(key));
        }

        /* write */ multimap.put("key", 42);

        // Adding more entries to the multimap
        multimap.put("key", 43);
        multimap.put("anotherKey", 100);

        // Performing operations on the multimap
        for (String key : multimap.keySet()) {
            System.out.println(key + ": " + multimap.get(key));
        }

        // Unrelated computation
        int sum = 0;
        for (int i = 0; i < 10; i++) {
            sum += i;
        }
        System.out.println("Sum: " + sum);

        /* read */ Integer value = multimap.get("key").get(0);
        System.out.println(value);

        // More unrelated operations
        int product = 1;
        for (int i = 1; i <= 5; i++) {
            product *= i;
        }
        System.out.println("Product: " + product);
    }
}
