import com.google.common.collect.MutableClassToInstanceMap;
import com.google.common.collect.ClassToInstanceMap;
import com.google.common.collect.ImmutableClassToInstanceMap;
import java.util.HashMap;
import java.util.Map;

public class Main {
    public static void main(String[] args) {
        MutableClassToInstanceMap<Object> map = MutableClassToInstanceMap.create();
        Map<String, Integer> simpleMap = new HashMap<>();
        simpleMap.put("one", 1);
        simpleMap.put("two", 2);
        simpleMap.put("three", 3);

        ClassToInstanceMap<Object> immutableMap = ImmutableClassToInstanceMap.builder()
                .put(Integer.class, 42)
                .put(String.class, "Immutable String")
                .build();

        /* write */ map.putInstance(String.class, "Hello, World!");

        // Operations on the simple map
        int sum = 0;
        for (String key : simpleMap.keySet()) {
            sum += simpleMap.get(key);
        }
        System.out.println("Sum of values in simpleMap: " + sum);

        // Using the immutable map
        Integer immutableValue = immutableMap.getInstance(Integer.class);
        System.out.println("Value from immutableMap: " + immutableValue);

        // More complex operations
        String greeting = "Greetings!";
        map.putInstance(String.class, greeting);

        /* read */ String value = map.getInstance(String.class);
        System.out.println(value);

        // Final operations
        simpleMap.put("four", 4);
        for (String key : simpleMap.keySet()) {
            System.out.println("Key: " + key + ", Value: " + simpleMap.get(key));
        }
    }
}
