import org.jfree.chart.labels.StandardXYItemLabelGenerator;
import java.util.ArrayList;
import java.util.List;

public class Main {
    public static void main(String[] args) {
        StandardXYItemLabelGenerator labelGenerator = new StandardXYItemLabelGenerator();
        /* write */ labelGenerator.setFormatString("{0}: {1}");

        // Additional logic to make the program more complex
        List<String> dataPoints = new ArrayList<>();
        dataPoints.add("Point A");
        dataPoints.add("Point B");
        dataPoints.add("Point C");

        for (String point : dataPoints) {
            System.out.println("Processing " + point);
        }

        // Simulate some complex operations
        int sum = 0;
        for (int i = 0; i < 10; i++) {
            sum += i;
        }
        System.out.println("Sum of first 10 numbers: " + sum);

        // Use similar API pair
        StandardXYItemLabelGenerator anotherLabelGenerator = new StandardXYItemLabelGenerator();
        anotherLabelGenerator.setFormatString("{2}: {3}");
        String anotherFormatString = anotherLabelGenerator.getFormatString();
        System.out.println("Another format string: " + anotherFormatString);

        // Further unrelated operations
        int product = 1;
        for (int i = 1; i <= 5; i++) {
            product *= i;
        }
        System.out.println("Product of first 5 numbers: " + product);

        // Read the format string from the original label generator
        /* read */ String formatString = labelGenerator.getFormatString();
        System.out.println("Original format string: " + formatString);
    }
