import org.apache.commons.lang3.text.StrBuilder;
import java.util.ArrayList;

public class Main {
    public static void main(String[] args) {
        StrBuilder strBuilder = new StrBuilder();
        ArrayList<Integer> numbers = new ArrayList<>();
        ArrayList<String> strings = new ArrayList<>();

        // Adding numbers to the list
        for (int i = 0; i < 5; i++) {
            numbers.add(i * 2);
        }

        // Adding strings to the list
        strings.add("first");
        strings.add("second");
        strings.add("third");

        // Using StrBuilder to append a separator
        /* write */ strBuilder.appendSeparator(", ", strings.get(0));

        // Adding more elements to the StrBuilder
        for (String str : strings) {
            strBuilder.append(str).append(", ");
        }

        // Processing numbers list
        int sum = 0;
        for (int num : numbers) {
            sum += num;
        }
        System.out.println("Sum of numbers: " + sum);

        // More operations with StrBuilder
        strBuilder.append("final");
        StringBuilder additionalBuilder = new StringBuilder();
        additionalBuilder.append("additional");
        strBuilder.append(additionalBuilder.toString());

        // Reading the final result from StrBuilder
        /* read */ String result = strBuilder.toString();
        System.out.println(result);

        // Further processing with numbers list
        for (int i = 0; i < numbers.size(); i++) {
            numbers.set(i, numbers.get(i) + 10);
        }

        // Display updated numbers
        for (int num : numbers) {
            System.out.println("Updated number: " + num);
        }
    }
}
