import org.apache.commons.lang3.text.StrBuilder;
import java.util.ArrayList;
import java.util.List;

public class Main {
    public static void main(String[] args) {
        StrBuilder strBuilder = new StrBuilder("Hello World!");

        // Additional code to make the program more complex
        List<String> words = new ArrayList<>();
        words.add("Java");
        words.add("Programming");
        words.add("Language");

        for (String word : words) {
            System.out.println("Processing word: " + word);
            strBuilder.append(" ").append(word);
        }

        /* write */ strBuilder.deleteCharAt(5);

        int length = strBuilder.length();
        System.out.println("Length of StrBuilder: " + length);

        String substring = strBuilder.substring(0, 5);
        System.out.println("Substring: " + substring);

        // Further manipulation of the StrBuilder
        strBuilder.insert(0, "Welcome to ");

        // Additional unrelated code
        int sum = 0;
        for (int i = 0; i < 10; i++) {
            sum += i;
        }
        System.out.println("Sum of numbers from 0 to 9: " + sum);

        /* read */ String result = strBuilder.toString();
        System.out.println(result);

        // Continue with more complex operations
        strBuilder.replace(0, 7, "Hello");
        System.out.println("Final result: " + strBuilder.toString());
    }
}
