import org.apache.commons.lang3.text.StrBuilder;

public class Main {
    public static void main(String[] args) {
        StrBuilder strBuilder = new StrBuilder();
        StrBuilder anotherStrBuilder = new StrBuilder();

        // Adding some initial content to anotherStrBuilder
        anotherStrBuilder.append("InitialContent");

        // Appending separators and some text to strBuilder
        /* write */ strBuilder.appendSeparator(",");
        strBuilder.append("Hello");
        strBuilder.appendSeparator(",");
        strBuilder.append("World");

        // Performing operations on anotherStrBuilder
        anotherStrBuilder.appendSeparator("-");
        anotherStrBuilder.append("MoreContent");

        // Concatenating anotherStrBuilder to strBuilder
        strBuilder.append(anotherStrBuilder);

        // Some unrelated operations
        int sum = 0;
        for (int i = 0; i < 10; i++) {
            sum += i;
        }
        System.out.println("Sum of numbers from 0 to 9: " + sum);

        // More complex operations
        int product = 1;
        for (int i = 1; i <= 5; i++) {
            product *= i;
        }
        System.out.println("Product of numbers from 1 to 5: " + product);

        // Final read operation
        /* read */ String result = strBuilder.toString();
        System.out.println("StrBuilder content: " + result);
    }
}
