        double[][] studentGrades = {
            {85.0, 90.0, 95.0},
            {80.0, 85.0, 90.0},
            {75.0, 80.0, 85.0}
        };

        Student student = new Student("John Doe", studentGrades);
        System.out.println("Student Name: " + student.getName());
