import org.apache.commons.collections4.list.NodeCachingLinkedList;
import java.util.ArrayList;
import java.util.HashMap;

public class Main {
    public static void main(String[] args) {
        NodeCachingLinkedList<Integer> list = new NodeCachingLinkedList<>();
        ArrayList<String> stringList = new ArrayList<>();
        HashMap<Integer, String> map = new HashMap<>();

        // Adding elements to stringList
        stringList.add("Hello");
        stringList.add("World");

        // Adding elements to map
        map.put(1, "One");
        map.put(2, "Two");

        // Perform operations on stringList
        if (!stringList.isEmpty()) {
            System.out.println("First element in stringList: " + stringList.get(0));
        }

        // Perform operations on map
        if (map.containsKey(1)) {
            System.out.println("Value for key 1 in map: " + map.get(1));
        }

        /* write */ list.add(42);

        // Perform more operations on stringList
        stringList.add("Java");
        for (String str : stringList) {
            System.out.println("StringList item: " + str);
        }

        // Perform more operations on map
        map.put(3, "Three");
        for (Integer key : map.keySet()) {
            System.out.println("Map key: " + key + ", value: " + map.get(key));
        }

        // Continue with operations on list
        int sum = 0;
        for (int i = 0; i < 5; i++) {
            list.add(i * 10);
            sum += i * 10;
        }
        System.out.println("Sum of added elements: " + sum);

        /* read */ int value = list.get(0);
        System.out.println("Retrieved value from list: " + value);

        // Additional operations
        System.out.println("List size: " + list.size());
        for (int i = 0; i < list.size(); i++) {
            System.out.println("List element at index " + i + ": " + list.get(i));
        }
    }
}
