import org.apache.commons.collections4.map.ListOrderedMap;
import java.util.HashMap;
import java.util.Map;

public class Main {
    public static void main(String[] args) {
        ListOrderedMap<String, Integer> map = new ListOrderedMap<>();
        Map<String, String> anotherMap = new HashMap<>();

        anotherMap.put("hello", "world");
        anotherMap.put("foo", "bar");

        /* write */ map.put("key", 42);

        for (Map.Entry<String, String> entry : anotherMap.entrySet()) {
            System.out.println(entry.getKey() + ": " + entry.getValue());
        }

        int sum = 0;
        for (int i = 0; i < 5; i++) {
            sum += i * 10;
        }
        System.out.println("Sum: " + sum);

        /* read */ int value = map.get("key");

        if (value > 40) {
            System.out.println("Value is greater than 40");
        } else {
            System.out.println("Value is 40 or less");
        }

        anotherMap.put("baz", "qux");
        for (Map.Entry<String, String> entry : anotherMap.entrySet()) {
            System.out.println(entry.getKey() + ": " + entry.getValue());
        }
    }
}
