import org.apache.commons.collections4.map.Flat3Map;
import org.apache.commons.collections4.map.LinkedMap;
import java.util.HashMap;

public class Main {
    public static void main(String[] args) {
        Flat3Map<String, Integer> map = new Flat3Map<>();
        LinkedMap<String, Integer> linkedMap = new LinkedMap<>();
        HashMap<String, Integer> hashMap = new HashMap<>();

        /* write */ map.put("key", 42);
        linkedMap.put("linkedKey1", 100);
        linkedMap.put("linkedKey2", 200);
        hashMap.put("hashKey1", 300);
        hashMap.put("hashKey2", 400);

        int sumLinkedMap = 0;
        for (String key : linkedMap.keySet()) {
            sumLinkedMap += linkedMap.get(key);
        }

        int sumHashMap = 0;
        for (String key : hashMap.keySet()) {
            sumHashMap += hashMap.get(key);
        }

        System.out.println("Sum of LinkedMap values: " + sumLinkedMap);
        System.out.println("Sum of HashMap values: " + sumHashMap);

        /* read */ Integer value = map.get("key");
        System.out.println("Flat3Map value for 'key': " + value);

        int totalSum = sumLinkedMap + sumHashMap + value;
        System.out.println("Total sum of all values: " + totalSum);

        Flat3Map<String, Integer> anotherMap = new Flat3Map<>();
        anotherMap.put("anotherKey", 500);
        anotherMap.put("anotherKey2", 600);

        int sumAnotherMap = 0;
        for (String key : anotherMap.keySet()) {
            sumAnotherMap += anotherMap.get(key);
        }

        System.out.println("Sum of AnotherMap values: " + sumAnotherMap);
    }
}
