import org.apache.commons.collections4.map.MultiValueMap;
import java.util.HashMap;
import java.util.Map;

public class Main {
    public static void main(String[] args) {
        MultiValueMap<String, Integer> multiValueMap = new MultiValueMap<>();
        Map<String, String> hashMap = new HashMap<>();

        hashMap.put("name", "Alice");
        hashMap.put("age", "30");
        hashMap.put("city", "Wonderland");

        /* write */ multiValueMap.put("key", 42);

        for (Map.Entry<String, String> entry : hashMap.entrySet()) {
            System.out.println(entry.getKey() + ": " + entry.getValue());
        }

        int sum = 0;
        for (int i = 1; i <= 10; i++) {
            sum += i;
            multiValueMap.put("numbers", i);
        }
        System.out.println("Sum of numbers from 1 to 10: " + sum);

        /* read */ Integer value = (Integer) multiValueMap.get("key").iterator().next();
        System.out.println("Read value from MultiValueMap: " + value);

        for (Object number : multiValueMap.get("numbers")) {
            System.out.println("Number from MultiValueMap: " + number);
        }

        Map<String, Double> priceMap = new HashMap<>();
        priceMap.put("apple", 1.99);
        priceMap.put("banana", 0.99);
        priceMap.put("cherry", 2.99);

        double totalPrice = 0.0;
        for (Map.Entry<String, Double> entry : priceMap.entrySet()) {
            totalPrice += entry.getValue();
            System.out.println("Price of " + entry.getKey() + ": $" + entry.getValue());
        }
        System.out.println("Total price: $" + totalPrice);
    }
}
