import org.apache.commons.compress.archivers.tar.TarArchiveEntry;
import java.util.HashMap;
import java.util.Map;

public class Main {
    public static void main(String[] args) {
        TarArchiveEntry entry = new TarArchiveEntry("example.txt");
        /* write */ entry.setMode(0644);

        // Adding some unrelated complex code
        Map<String, Integer> filePermissions = new HashMap<>();
        filePermissions.put("example.txt", entry.getMode());
        filePermissions.put("anotherFile.txt", 0755);

        int sumOfModes = 0;
        for (Map.Entry<String, Integer> fileEntry : filePermissions.entrySet()) {
            System.out.println("File: " + fileEntry.getKey() + ", Mode: " + fileEntry.getValue());
            sumOfModes += fileEntry.getValue();
        }

        System.out.println("Sum of all modes: " + sumOfModes);

        // More unrelated complex code
        int complexCalculationResult = 0;
        for (int i = 0; i < 10; i++) {
            complexCalculationResult += i * 2;
        }
        System.out.println("Result of complex calculation: " + complexCalculationResult);

        /* read */ int mode = entry.getMode();
        System.out.println("Mode of 'example.txt': " + mode);

        // Additional unrelated code
        int[] array = {1, 2, 3, 4, 5};
        int arraySum = 0;
        for (int num : array) {
            arraySum += num;
        }
        System.out.println("Sum of array elements: " + arraySum);
    }
}
