import org.apache.commons.compress.compressors.gzip.GzipParameters;
import java.util.ArrayList;
import java.util.HashMap;

public class Main {
    public static void main(String[] args) {
        // Create GzipParameters object
        GzipParameters gzipParameters = new GzipParameters();

        // Create a list to store operating system codes
        ArrayList<Integer> osCodes = new ArrayList<>();
        osCodes.add(1);
        osCodes.add(2);
        osCodes.add(3);
        osCodes.add(4);

        // Create a map to store operating system names
        HashMap<Integer, String> osNames = new HashMap<>();
        osNames.put(1, "Windows");
        osNames.put(2, "Linux");
        osNames.put(3, "MacOS");
        osNames.put(4, "Unix");

        // Iterate over the list and set the operating system in gzipParameters
        for (int code : osCodes) {
            if (code == 3) {
                /* write */ gzipParameters.setOperatingSystem(code);
            }
        }

        // Perform some unrelated calculations
        int sum = 0;
        for (int i = 0; i < 10; i++) {
            sum += i * i;
        }
        System.out.println("Sum of squares: " + sum);

        // Retrieve the operating system code from gzipParameters
        /* read */ int operatingSystem = gzipParameters.getOperatingSystem();

        // Print the operating system name using the retrieved code
        String osName = osNames.get(operatingSystem);
        System.out.println("Operating System: " + osName);

        // More complex operations
        ArrayList<String> messages = new ArrayList<>();
        for (int i = 0; i < 5; i++) {
            messages.add("Message " + i);
        }

        for (String message : messages) {
            System.out.println(message);
        }

        // Final operations
        int factorial = 1;
        for (int i = 1; i <= 5; i++) {
            factorial *= i;
        }
        System.out.println("Factorial of 5: " + factorial);
    }
}
