import org.apache.commons.compress.archivers.zip.ZipArchiveEntry;
import java.util.ArrayList;
import java.util.HashMap;
import java.util.Map;

public class Main {
    public static void main(String[] args) {
        ZipArchiveEntry entry = new ZipArchiveEntry("example.txt");
        /* write */ entry.setComment("This is a comment.");

        // Create a list of ZipArchiveEntries
        ArrayList<ZipArchiveEntry> entries = new ArrayList<>();
        entries.add(entry);
        entries.add(new ZipArchiveEntry("another_example.txt"));

        // Create a map to store file names and their sizes
        Map<String, Integer> fileSizes = new HashMap<>();
        fileSizes.put("example.txt", 1024);
        fileSizes.put("another_example.txt", 2048);

        // Iterate over the entries and print their details
        for (ZipArchiveEntry e : entries) {
            System.out.println("File: " + e.getName());
            if (fileSizes.containsKey(e.getName())) {
                System.out.println("Size: " + fileSizes.get(e.getName()) + " bytes");
            }
        }

        // Perform some unrelated computations
        int sum = 0;
        for (int i = 1; i <= 10; i++) {
            sum += i;
        }
        System.out.println("Sum of numbers from 1 to 10 is: " + sum);

        // Now read the comment from the entry
        /* read */ String comment = entry.getComment();
        System.out.println("Comment: " + comment);

        // Another unrelated computation
        int product = 1;
        for (int i = 1; i <= 5; i++) {
            product *= i;
        }
        System.out.println("Product of numbers from 1 to 5 is: " + product);
    }
}
