import org.apache.commons.compress.archivers.tar.TarArchiveEntry;
import org.apache.commons.compress.archivers.tar.TarArchiveOutputStream;
import org.apache.commons.compress.archivers.tar.TarArchiveInputStream;
import java.io.ByteArrayOutputStream;
import java.io.ByteArrayInputStream;
import java.io.IOException;

public class Main {
    public static void main(String[] args) {
        TarArchiveEntry entry = new TarArchiveEntry("example.txt");
        TarArchiveEntry entry2 = new TarArchiveEntry("example2.txt");
        TarArchiveEntry entry3 = new TarArchiveEntry("example3.txt");

        /* write */ entry.setUserName("exampleUser");

        entry2.setUserName("anotherUser");
        entry3.setUserName("thirdUser");

        ByteArrayOutputStream baos = new ByteArrayOutputStream();
        try {
            TarArchiveOutputStream taos = new TarArchiveOutputStream(baos);
            taos.putArchiveEntry(entry);
            taos.putArchiveEntry(entry2);
            taos.putArchiveEntry(entry3);
            taos.closeArchiveEntry();
            taos.close();

            ByteArrayInputStream bais = new ByteArrayInputStream(baos.toByteArray());
            TarArchiveInputStream tais = new TarArchiveInputStream(bais);

            TarArchiveEntry readEntry;
            while ((readEntry = tais.getNextTarEntry()) != null) {
                System.out.println("Reading entry: " + readEntry.getName());
            }
            tais.close();

        } catch (IOException e) {
            e.printStackTrace();
        }

        /* read */ String userName = entry.getUserName();
        System.out.println("Original entry username: " + userName);

        System.out.println("Second entry username: " + entry2.getUserName());
        System.out.println("Third entry username: " + entry3.getUserName());
    }
}
