import org.apache.commons.compress.archivers.zip.ZipArchiveEntry;
import java.util.ArrayList;
import java.util.List;

public class Main {
    public static void main(String[] args) {
        // Initialize a ZipArchiveEntry and set its CRC value
        ZipArchiveEntry zipEntry = new ZipArchiveEntry("example.txt");

        /* write */ zipEntry.setCrc(123456789L);

        // Create an ArrayList to store multiple ZipArchiveEntries
        List<ZipArchiveEntry> zipEntries = new ArrayList<>();
        zipEntries.add(zipEntry);

        // Add additional entries with different CRC values
        ZipArchiveEntry zipEntry2 = new ZipArchiveEntry("example2.txt");
        zipEntry2.setCrc(987654321L);
        zipEntries.add(zipEntry2);

        ZipArchiveEntry zipEntry3 = new ZipArchiveEntry("example3.txt");
        zipEntry3.setCrc(192837465L);
        zipEntries.add(zipEntry3);

        // Iterate over the list and print CRC values of all entries
        for (ZipArchiveEntry entry : zipEntries) {
            System.out.println("CRC of " + entry.getName() + ": " + entry.getCrc());
        }

        // Perform some unrelated operations to make the program more complex
        int sum = 0;
        for (int i = 0; i < 10; i++) {
            sum += i;
        }
        System.out.println("Sum of numbers from 0 to 9: " + sum);

        // Calculate the average CRC value of all entries
        long totalCrc = 0;
        for (ZipArchiveEntry entry : zipEntries) {
            totalCrc += entry.getCrc();
        }
        double averageCrc = totalCrc / zipEntries.size();
        System.out.println("Average CRC value: " + averageCrc);

        /* read */ long crcValue = zipEntry.getCrc();

        // Print the CRC value of the first ZipArchiveEntry
        System.out.println("CRC of the first entry: " + crcValue);
    }
}
