import org.apache.commons.compress.compressors.gzip.GzipParameters;
import java.util.ArrayList;
import java.util.HashMap;

public class Main {
    public static void main(String[] args) {
        GzipParameters gzipParameters = new GzipParameters();
        /* write */ gzipParameters.setFilename("example.txt");

        // Additional code to increase complexity
        ArrayList<String> filenames = new ArrayList<>();
        HashMap<String, Integer> filenameOccurrences = new HashMap<>();

        filenames.add("example.txt");
        filenames.add("test.txt");
        filenames.add("data.txt");

        for (String name : filenames) {
            filenameOccurrences.put(name, name.length());
        }

        int totalLength = 0;
        for (String name : filenames) {
            totalLength += filenameOccurrences.get(name);
        }

        System.out.println("Total length of filenames: " + totalLength);

        // More unrelated operations
        int sum = 0;
        for (int i = 0; i < 10; i++) {
            sum += i;
        }
        System.out.println("Sum of numbers from 0 to 9: " + sum);

        /* read */ String filename = gzipParameters.getFilename();
        System.out.println(filename);

        // Additional operations
        ArrayList<Integer> numbers = new ArrayList<>();
        for (int i = 0; i < 5; i++) {
            numbers.add(i * 10);
        }

        for (int num : numbers) {
            System.out.println("Number: " + num);
        }
    }
}
