import org.apache.commons.compress.compressors.gzip.GzipParameters;
import java.util.ArrayList;
import java.util.HashMap;

public class Main {
    public static void main(String[] args) {
        // Initialize GzipParameters
        GzipParameters gzipParameters = new GzipParameters();

        // Create and manipulate an ArrayList
        ArrayList<Integer> numbers = new ArrayList<>();
        for (int i = 0; i < 5; i++) {
            numbers.add(i * 10);
        }

        // Initialize a HashMap
        HashMap<String, Integer> map = new HashMap<>();
        map.put("One", 1);
        map.put("Two", 2);

        // Use a loop to print values from the ArrayList
        for (Integer number : numbers) {
            System.out.println("Number: " + number);
        }

        // Set operating system in GzipParameters
        /* write */ gzipParameters.setOperatingSystem(3);

        // Perform operations with HashMap
        if (map.containsKey("Two")) {
            System.out.println("Map contains key 'Two' with value: " + map.get("Two"));
        }

        // Perform additional operations on ArrayList
        numbers.add(50);
        numbers.remove(0);

        // Retrieve and print the operating system from GzipParameters
        /* read */ int operatingSystem = gzipParameters.getOperatingSystem();
        System.out.println("Operating System: " + operatingSystem);

        // Further manipulation of HashMap
        map.put("Three", 3);
        for (String key : map.keySet()) {
            System.out.println("Key: " + key + ", Value: " + map.get(key));
        }
    }
}
