import com.google.common.collect.HashBasedTable;
import com.google.common.collect.Table;
import java.util.HashMap;
import java.util.Map;

public class Main {
    public static void main(String[] args) {
        // Initialize a HashBasedTable
        Table<String, String, Integer> table = HashBasedTable.create();

        // Initialize a HashMap for additional complexity
        Map<String, Integer> map = new HashMap<>();

        // Populate the map with some data
        map.put("key1", 100);
        map.put("key2", 200);

        // Perform some operations on the map
        for (String key : map.keySet()) {
            map.put(key, map.get(key) + 50);
        }

        // Use the write method on the table
        /* write */ table.put("row1", "column1", 42);

        // Add more entries to the table
        table.put("row2", "column1", 84);
        table.put("row1", "column2", 126);

        // Perform operations using table data
        int sum = 0;
        for (String row : table.rowKeySet()) {
            for (String column : table.columnKeySet()) {
                Integer value = table.get(row, column);
                if (value != null) {
                    sum += value;
                }
            }
        }

        // Print the sum of all values in the table
        System.out.println("Sum of values in table: " + sum);

        // Perform some operations on map values
        for (Integer value : map.values()) {
            System.out.println("Modified map value: " + value);
        }

        // Use the read method on the table
        /* read */ Integer value = table.get("row1", "column1");

        // Print the value retrieved from the table
        System.out.println("Value from table: " + value);

        // Add more complexity with calculations
        int product = 1;
        for (int i = 1; i <= 5; i++) {
            product *= i;
        }
        System.out.println("Product of numbers 1 to 5: " + product);
    }
}
