import com.google.common.collect.TreeMultimap;
import java.util.HashMap;
import java.util.Map;

public class Main {
    public static void main(String[] args) {
        TreeMultimap<String, Integer> multimap = TreeMultimap.create();
        Map<String, String> map = new HashMap<>();

        /* write */ multimap.put("key", 42);

        map.put("name", "Alice");
        map.put("age", "30");

        int sum = 0;
        for (int i = 0; i < 5; i++) {
            sum += i;
        }

        for (Map.Entry<String, String> entry : map.entrySet()) {
            System.out.println(entry.getKey() + ": " + entry.getValue());
        }

        int product = 1;
        for (int i = 1; i <= 3; i++) {
            product *= i;
        }

        System.out.println("Sum: " + sum);
        System.out.println("Product: " + product);

        /* read */ Integer value = multimap.get("key").iterator().next();

        System.out.println("Value from multimap: " + value);

        Map<Integer, String> reverseMap = new HashMap<>();
        reverseMap.put(1, "One");
        reverseMap.put(2, "Two");
        reverseMap.put(3, "Three");

        for (int i = 1; i <= 3; i++) {
            System.out.println("Number " + i + " is " + reverseMap.get(i));
        }
    }
}
