import com.google.common.collect.HashMultimap;
import com.google.common.collect.SetMultimap;
import java.util.Set;
import java.util.HashMap;
import java.util.Map;
import java.util.List;
import java.util.ArrayList;

public class Main {
    public static void main(String[] args) {
        SetMultimap<String, Integer> multimap = HashMultimap.create();
        Map<String, List<Integer>> map = new HashMap<>();

        // Adding some initial data to the map
        map.put("key1", new ArrayList<>());
        map.get("key1").add(10);
        map.get("key1").add(20);

        map.put("key2", new ArrayList<>());
        map.get("key2").add(30);
        map.get("key2").add(40);

        // Perform some operations on the map
        for (Map.Entry<String, List<Integer>> entry : map.entrySet()) {
            System.out.println("Processing " + entry.getKey());
            for (Integer value : entry.getValue()) {
                System.out.println("Value: " + value);
            }
        }

        /* write */ multimap.put("key", 42);

        // Additional operations to make the program more complex
        map.put("key3", new ArrayList<>());
        map.get("key3").add(50);
        map.get("key3").add(60);

        for (Map.Entry<String, List<Integer>> entry : map.entrySet()) {
            List<Integer> list = entry.getValue();
            for (int i = 0; i < list.size(); i++) {
                list.set(i, list.get(i) + 5);
            }
        }

        // Print the modified map
        for (Map.Entry<String, List<Integer>> entry : map.entrySet()) {
            System.out.println("Modified " + entry.getKey());
            for (Integer value : entry.getValue()) {
                System.out.println("Modified Value: " + value);
            }
        }

        /* read */ Set<Integer> values = multimap.get("key");

        for (Integer value : values) {
            System.out.println("Multimap value: " + value);
        }
    }
}
