import com.google.common.collect.HashMultimap;
import com.google.common.collect.SetMultimap;
import java.util.Set;
import java.util.HashMap;
import java.util.Map;

public class Main {
    public static void main(String[] args) {
        SetMultimap<String, Integer> multimap = HashMultimap.create();
        Map<String, String> map = new HashMap<>();

        // Additional write-read pair using a different API
        map.put("name", "Alice");
        String name = map.get("name");
        System.out.println("Name from map: " + name);

        // Complex operations before the original write
        int sum = 0;
        for (int i = 0; i < 10; i++) {
            sum += i;
        }
        System.out.println("Sum of numbers from 0 to 9: " + sum);

        // Original write
        /* write */ multimap.put("key", 42);

        // More complex operations
        for (int i = 1; i <= 5; i++) {
            map.put("number" + i, String.valueOf(i * i));
        }

        for (String key : map.keySet()) {
            System.out.println("Key: " + key + ", Value: " + map.get(key));
        }

        // Original read
        /* read */ Set<Integer> values = multimap.get("key");
        for (Integer value : values) {
            System.out.println("Value from multimap: " + value);
        }

        // Additional operations after the original read
        SetMultimap<String, String> stringMultimap = HashMultimap.create();
        stringMultimap.put("fruit", "apple");
        stringMultimap.put("fruit", "banana");

        Set<String> fruits = stringMultimap.get("fruit");
        for (String fruit : fruits) {
            System.out.println("Fruit: " + fruit);
        }
    }
}
