import com.google.common.collect.HashBasedTable;
import com.google.common.collect.Table;
import java.util.HashMap;
import java.util.Map;

public class Main {
    public static void main(String[] args) {
        Table<String, String, Integer> table = HashBasedTable.create();
        Map<String, Integer> map = new HashMap<>();

        /* write */ table.put("row1", "column1", 42);

        // Adding more entries to the table
        table.put("row2", "column2", 84);
        table.put("row3", "column3", 126);

        // Adding entries to the map
        map.put("key1", 100);
        map.put("key2", 200);
        map.put("key3", 300);

        // Accessing and printing values from the map
        for (String key : map.keySet()) {
            System.out.println("Map value for " + key + ": " + map.get(key));
        }

        // Performing operations on the table
        Integer sum = 0;
        for (Table.Cell<String, String, Integer> cell : table.cellSet()) {
            sum += cell.getValue();
        }
        System.out.println("Sum of all table values: " + sum);

        // Accessing the original write-read pair
        /* read */ Integer value = table.get("row1", "column1");
        System.out.println("Read value from table: " + value);

        // Performing additional operations
        map.put("key4", sum);
        System.out.println("Updated map with sum: " + map.get("key4"));

        // Further manipulation of the table
        table.put("row4", "column4", map.get("key4"));
        System.out.println("New value in table: " + table.get("row4", "column4"));
    }
}
