import com.google.common.collect.HashBasedTable;
import com.google.common.collect.Table;
import java.util.HashMap;
import java.util.Map;

public class Main {
    public static void main(String[] args) {
        // Initialize a Guava Table
        Table<String, String, Integer> table = HashBasedTable.create();

        // Initialize a HashMap
        Map<String, Integer> map = new HashMap<>();

        // Populate the HashMap
        map.put("key1", 100);
        map.put("key2", 200);

        // Use the HashMap
        int mapValue = map.get("key1");
        System.out.println("Value from HashMap: " + mapValue);

        // Add more data to the Table
        table.put("row2", "column2", 84);
        table.put("row3", "column3", 126);

        // Perform operations on the table
        if (table.contains("row2", "column2")) {
            Integer tableValue = table.get("row2", "column2");
            System.out.println("Value from Table (row2, column2): " + tableValue);
        }

        // Perform some calculations
        int sum = 0;
        for (Map.Entry<String, Integer> entry : map.entrySet()) {
            sum += entry.getValue();
        }
        System.out.println("Sum of values in HashMap: " + sum);

        // Original write operation
        /* write */ table.put("row1", "column1", 42);

        // More operations
        for (Table.Cell<String, String, Integer> cell : table.cellSet()) {
            System.out.println("Table cell: (" + cell.getRowKey() + ", " + cell.getColumnKey() + ") = " + cell.getValue());
        }

        // Original read operation
        /* read */ Integer value = table.get("row1", "column1");
        System.out.println("Read value from Table (row1, column1): " + value);

        // Final operations
        int totalSum = sum + value;
        System.out.println("Total sum: " + totalSum);
    }
}
