        basket.addFruit("apple", 3);
        basket.addFruit("banana", 2);
        basket.addFruit("orange", 5);

        int totalFruits = 0;
        for (String fruit : basket.getFruitSummary().keySet()) {
            totalFruits += basket.getFruitCount(fruit);
