import com.google.common.collect.HashBasedTable;
import com.google.common.collect.Table;
import java.util.HashMap;
import java.util.Map;

public class Main {
    public static void main(String[] args) {
        Table<String, String, Integer> table = HashBasedTable.create();
        Map<String, Integer> hashMap = new HashMap<>();

        // Initialize HashMap with some values
        hashMap.put("key1", 100);
        hashMap.put("key2", 200);

        // Perform some operations on HashMap
        int sum = 0;
        for (Map.Entry<String, Integer> entry : hashMap.entrySet()) {
            sum += entry.getValue();
        }
        System.out.println("Sum of values in HashMap: " + sum);

        /* write */ table.put("row1", "column1", 42);

        // Additional operations on HashMap
        hashMap.put("key3", sum);
        System.out.println("Value for 'key3': " + hashMap.get("key3"));

        // More operations on the Table
        table.put("row2", "column2", 84);
        table.put("row3", "column3", 126);

        // Print all values from the table
        for (Table.Cell<String, String, Integer> cell : table.cellSet()) {
            System.out.println("Row: " + cell.getRowKey() + ", Column: " + cell.getColumnKey() + ", Value: " + cell.getValue());
        }

        /* read */ Integer value = table.get("row1", "column1");
        System.out.println("Read value from table: " + value);

        // Perform additional operations on Table
        Integer total = 0;
        for (Table.Cell<String, String, Integer> cell : table.cellSet()) {
            total += cell.getValue();
        }
        System.out.println("Total of all values in Table: " + total);
    }
}
