import com.google.common.collect.LinkedHashMultimap;
import com.google.common.collect.Multimap;
import java.util.HashMap;
import java.util.Map;

public class Main {
    public static void main(String[] args) {
        Multimap<String, Integer> multimap = LinkedHashMultimap.create();
        Map<String, String> hashMap = new HashMap<>();

        hashMap.put("name", "Alice");
        hashMap.put("city", "Wonderland");

        /* write */ multimap.put("key", 42);

        for (String key : hashMap.keySet()) {
            System.out.println(key + ": " + hashMap.get(key));
        }

        if (multimap.containsKey("key")) {
            System.out.println("Multimap contains the key.");
        }

        Integer sum = 0;
        for (Integer num : multimap.get("key")) {
            sum += num;
        }
        System.out.println("Sum of values in multimap for 'key': " + sum);

        /* read */ Integer value = multimap.get("key").iterator().next();
        System.out.println("Read value from multimap: " + value);

        Multimap<String, Integer> anotherMultimap = LinkedHashMultimap.create();
        for (int i = 0; i < 5; i++) {
            anotherMultimap.put("numbers", i * 10);
        }

        for (Integer num : anotherMultimap.get("numbers")) {
            System.out.println("Number from anotherMultimap: " + num);
        }
    }
}
