import com.google.common.collect.ArrayListMultimap;
import java.util.HashMap;
import java.util.Map;

public class Main {
    public static void main(String[] args) {
        ArrayListMultimap<String, Integer> multimap = ArrayListMultimap.create();
        Map<String, Integer> map = new HashMap<>();
        map.put("anotherKey", 100);

        // Adding some unrelated operations
        int sum = 0;
        for (int i = 0; i < 10; i++) {
            sum += i;
            map.put("key" + i, i * 10);
        }

        /* write */ multimap.put("key", 42);

        // More operations with the map
        for (Map.Entry<String, Integer> entry : map.entrySet()) {
            sum += entry.getValue();
        }

        // Operations with the multimap
        multimap.put("anotherKey", 200);
        multimap.put("anotherKey", 300);

        // Accessing data from the multimap
        Integer anotherValue = multimap.get("anotherKey").get(1);
        System.out.println("Another value from multimap: " + anotherValue);

        /* read */ Integer value = multimap.get("key").get(0);
        System.out.println(value);

        // Final operations
        if (sum > 100) {
            System.out.println("Sum is greater than 100");
        } else {
            System.out.println("Sum is less or equal to 100");
        }
    }
}
