import org.jfree.chart.LegendItem;
import org.jfree.chart.plot.PiePlot;
import org.jfree.chart.labels.StandardPieSectionLabelGenerator;
import org.jfree.chart.JFreeChart;

public class Main {
    public static void main(String[] args) {
        LegendItem legendItem = new LegendItem("Sample Legend");
        PiePlot plot = new PiePlot();
        plot.setLabelGenerator(new StandardPieSectionLabelGenerator());
        JFreeChart chart = new JFreeChart("Sample Chart", JFreeChart.DEFAULT_TITLE_FONT, plot, true);

        /* write */ legendItem.setSeriesIndex(5);

        // Additional complex operations
        for (int i = 0; i < 10; i++) {
            double angle = Math.toRadians(i * 36);
            double x = Math.cos(angle);
            double y = Math.sin(angle);
            plot.setSectionOutlinePaint(i, new java.awt.Color((int)(x * 255), (int)(y * 255), 128));
        }

        plot.setSectionPaint(0, java.awt.Color.RED);
        plot.setSectionPaint(1, java.awt.Color.GREEN);
        plot.setSectionPaint(2, java.awt.Color.BLUE);

        int seriesIndex = 0;
        for (int i = 0; i < 3; i++) {
            seriesIndex += i;
        }

        /* read */ seriesIndex = legendItem.getSeriesIndex();
        System.out.println(seriesIndex);

        // More operations
        double total = 0;
        for (int i = 0; i < 5; i++) {
            total += i * 1.5;
        }
        System.out.println("Total: " + total);

        plot.setSectionPaint(3, java.awt.Color.YELLOW);
        plot.setSectionPaint(4, java.awt.Color.ORANGE);
    }
}
