import org.jfree.chart.ChartPanel;
import org.jfree.chart.JFreeChart;
import java.util.ArrayList;

public class Main {
    public static void main(String[] args) {
        // Initialize a ChartPanel with a null chart
        ChartPanel chartPanel = new ChartPanel(null);

        // Initialize another ChartPanel with a sample chart
        JFreeChart sampleChart = new JFreeChart(null);
        ChartPanel anotherChartPanel = new ChartPanel(sampleChart);

        // Use an ArrayList to store ChartPanels
        ArrayList<ChartPanel> chartPanels = new ArrayList<>();
        chartPanels.add(chartPanel);
        chartPanels.add(anotherChartPanel);

        // Enable mouse wheel on the first ChartPanel
        /* write */ chartPanel.setMouseWheelEnabled(true);

        // Perform operations on the list of ChartPanels
        for (ChartPanel cp : chartPanels) {
            System.out.println("ChartPanel mouse wheel enabled: " + cp.isMouseWheelEnabled());
        }

        // Change mouse wheel setting on the second ChartPanel
        anotherChartPanel.setMouseWheelEnabled(false);

        // Check the mouse wheel setting of the first ChartPanel again
        /* read */ boolean isEnabled = chartPanel.isMouseWheelEnabled();
        System.out.println("First ChartPanel mouse wheel enabled: " + isEnabled);

        // Further operations
        for (int i = 0; i < chartPanels.size(); i++) {
            chartPanels.get(i).setMouseWheelEnabled(i % 2 == 0);
            System.out.println("ChartPanel " + i + " mouse wheel enabled: " + chartPanels.get(i).isMouseWheelEnabled());
        }
    }
}
