import org.jfree.chart.renderer.xy.XYShapeRenderer;
import org.jfree.chart.renderer.xy.XYLineAndShapeRenderer;
import java.util.ArrayList;

public class Main {
    public static void main(String[] args) {
        XYShapeRenderer shapeRenderer = new XYShapeRenderer();
        XYLineAndShapeRenderer lineRenderer = new XYLineAndShapeRenderer();

        ArrayList<Integer> values = new ArrayList<>();
        for (int i = 0; i < 5; i++) {
            values.add(i * 10);
        }

        for (int i = 0; i < values.size(); i++) {
            lineRenderer.setSeriesShapesVisible(i, true);
        }

        /* write */ shapeRenderer.setDrawOutlines(true);

        int sum = 0;
        for (int value : values) {
            sum += value;
        }
        System.out.println("Sum of values: " + sum);

        boolean allShapesVisible = true;
        for (int i = 0; i < values.size(); i++) {
            allShapesVisible = allShapesVisible && lineRenderer.getSeriesShapesVisible(i);
        }
        System.out.println("All series shapes visible: " + allShapesVisible);

        /* read */ boolean drawOutlines = shapeRenderer.getDrawOutlines();
        System.out.println("Draw outlines: " + drawOutlines);
    }
}
