import org.jfree.chart.title.TextTitle;
import java.util.ArrayList;

public class Main {
    public static void main(String[] args) {
        // Initialize a TextTitle object
        TextTitle title = new TextTitle();

        // Create an ArrayList to store some values
        ArrayList<String> stringList = new ArrayList<>();
        stringList.add("Java");
        stringList.add("Programming");
        stringList.add("Complexity");

        // Set text for the title
        /* write */ title.setText("Hello World");

        // Print values from stringList
        for (String str : stringList) {
            System.out.println("String from list: " + str);
        }

        // Perform some mathematical operations
        int sum = 0;
        for (int i = 1; i <= 10; i++) {
            sum += i;
        }
        System.out.println("Sum of numbers from 1 to 10: " + sum);

        // Retrieve and print the text from the title
        /* read */ String text = title.getText();
        System.out.println("Title text: " + text);

        // Add more elements to stringList
        stringList.add("API");
        stringList.add("Integration");

        // Print all elements from stringList
        for (String str : stringList) {
            System.out.println("Updated list element: " + str);
        }

        // Another mathematical operation
        int product = 1;
        for (int i = 1; i <= 5; i++) {
            product *= i;
        }
        System.out.println("Product of numbers from 1 to 5: " + product);
    }
}
