import org.jfree.chart.plot.PiePlot;
import org.jfree.chart.plot.CategoryPlot;
import org.jfree.chart.axis.NumberAxis;
import org.jfree.chart.axis.CategoryAxis;

public class Main {
    public static void main(String[] args) {
        PiePlot piePlot = new PiePlot();
        CategoryPlot categoryPlot = new CategoryPlot();

        // Initialize axes for category plot
        NumberAxis rangeAxis = new NumberAxis("Value");
        CategoryAxis domainAxis = new CategoryAxis("Category");
        categoryPlot.setRangeAxis(rangeAxis);
        categoryPlot.setDomainAxis(domainAxis);

        // Set some properties for the category plot
        categoryPlot.setRangeGridlinesVisible(true);
        categoryPlot.setDomainGridlinesVisible(true);

        // Set maximum label width for pie plot
        /* write */ piePlot.setMaximumLabelWidth(0.2);

        // Perform some operations with category plot
        rangeAxis.setAutoRangeIncludesZero(false);
        domainAxis.setCategoryMargin(0.1);

        // Read maximum label width from pie plot
        /* read */ double maxLabelWidth = piePlot.getMaximumLabelWidth();
        System.out.println("Maximum Label Width: " + maxLabelWidth);

        // More operations on category plot
        double lowerBound = rangeAxis.getLowerBound();
        double upperBound = rangeAxis.getUpperBound();
        System.out.println("Range Axis Lower Bound: " + lowerBound);
        System.out.println("Range Axis Upper Bound: " + upperBound);

        // Adjust some settings
        categoryPlot.setOutlineVisible(true);
        categoryPlot.setBackgroundAlpha(0.5f);

        // Final adjustments
        piePlot.setSectionOutlinesVisible(true);
        piePlot.setIgnoreZeroValues(false);
    }
}
