import org.jfree.chart.renderer.xy.StackedXYBarRenderer;
import org.jfree.chart.renderer.xy.XYBarRenderer;
import java.util.ArrayList;

public class Main {
    public static void main(String[] args) {
        StackedXYBarRenderer renderer = new StackedXYBarRenderer();
        XYBarRenderer xyRenderer = new XYBarRenderer();
        ArrayList<Double> bases = new ArrayList<>();

        /* write */ renderer.setBase(5.0);

        xyRenderer.setBase(3.0);
        bases.add(xyRenderer.getBase());

        for (int i = 0; i < 5; i++) {
            xyRenderer.setBase(i * 2.0);
            bases.add(xyRenderer.getBase());
        }

        double sum = 0;
        for (double base : bases) {
            sum += base;
        }

        double averageBase = sum / bases.size();
        System.out.println("Average base value from xyRenderer: " + averageBase);

        /* read */ double baseValue = renderer.getBase();
        System.out.println("Base value from renderer: " + baseValue);

        if (baseValue > averageBase) {
            System.out.println("Renderer's base value is greater than the average base value.");
        } else {
            System.out.println("Renderer's base value is not greater than the average base value.");
        }
    }
}
