import org.jfree.chart.plot.ValueMarker;
import java.util.ArrayList;
import java.util.HashMap;

public class Main {
    public static void main(String[] args) {
        ValueMarker marker = new ValueMarker(0.0);
        ArrayList<Double> valuesList = new ArrayList<>();
        HashMap<String, Double> valuesMap = new HashMap<>();

        /* write */ marker.setValue(5.0);

        // Adding values to the list
        for (double i = 0.0; i < 5.0; i++) {
            valuesList.add(i);
        }

        // Adding values to the map
        for (int i = 0; i < valuesList.size(); i++) {
            valuesMap.put("Value " + i, valuesList.get(i));
        }

        // Perform some operations on the list
        double sum = 0.0;
        for (double val : valuesList) {
            sum += val;
        }
        System.out.println("Sum of list values: " + sum);

        // Accessing the value from the ValueMarker
        /* read */ double value = marker.getValue();
        System.out.println("Value from marker: " + value);

        // Perform some operations on the map
        for (String key : valuesMap.keySet()) {
            System.out.println(key + ": " + valuesMap.get(key));
        }

        // Additional operations on the list
        double average = sum / valuesList.size();
        System.out.println("Average of list values: " + average);
    }
}
