import org.jfree.chart.renderer.xy.XYDotRenderer;
import org.jfree.chart.renderer.xy.XYLineAndShapeRenderer;
import java.util.ArrayList;

public class Main {
    public static void main(String[] args) {
        XYDotRenderer renderer = new XYDotRenderer();
        XYLineAndShapeRenderer lineRenderer = new XYLineAndShapeRenderer();
        ArrayList<Integer> dotWidths = new ArrayList<>();

        // Setting dot width for the renderer
        /* write */ renderer.setDotWidth(5);

        // Additional operations
        lineRenderer.setSeriesShapesVisible(0, true);
        lineRenderer.setSeriesLinesVisible(1, false);

        // Adding some values to the list
        for (int i = 1; i <= 5; i++) {
            dotWidths.add(i * 2);
        }

        // Manipulating the list
        for (int i = 0; i < dotWidths.size(); i++) {
            System.out.println("Dot width from list: " + dotWidths.get(i));
        }

        // Retrieve the dot width from the renderer
        /* read */ int dotWidth = renderer.getDotWidth();
        System.out.println("Dot width from renderer: " + dotWidth);

        // Further operations
        int sum = 0;
        for (int width : dotWidths) {
            sum += width;
        }
        System.out.println("Sum of dot widths: " + sum);

        boolean isVisible = lineRenderer.getSeriesShapesVisible(0);
        System.out.println("Series 0 shapes visible: " + isVisible);
    }
}
