import org.jfree.chart.ChartPanel;
import org.jfree.chart.JFreeChart;
import org.jfree.chart.plot.PiePlot;

public class Main {
    public static void main(String[] args) {
        // Initialize a ChartPanel with a dummy JFreeChart
        PiePlot plot = new PiePlot();
        JFreeChart chart = new JFreeChart(plot);
        ChartPanel chartPanel = new ChartPanel(chart);

        // Additional code to make the program more complex
        // Set some properties on the chart
        plot.setCircular(true);
        plot.setLabelGenerator(null);

        // Perform operations on the chartPanel
        chartPanel.setPreferredSize(new java.awt.Dimension(500, 300));

        // Use a similar API pair
        chartPanel.setMouseZoomable(true);
        boolean isMouseZoomable = chartPanel.isMouseZoomable();
        System.out.println("Mouse Zoom Enabled: " + isMouseZoomable);

        // Additional unrelated code
        int[] numbers = {1, 2, 3, 4, 5};
        int sum = 0;
        for (int number : numbers) {
            sum += number;
        }
        System.out.println("Sum of numbers: " + sum);

        // Original write-read pair
        /* write */ chartPanel.setMouseWheelEnabled(true);

        // More unrelated code
        String[] words = {"Java", "Program", "Chart", "Panel"};
        for (String word : words) {
            System.out.println("Word: " + word);
        }

        /* read */ boolean isMouseWheelEnabled = chartPanel.isMouseWheelEnabled();
        System.out.println("Mouse Wheel Enabled: " + isMouseWheelEnabled);
    }
}
