import org.jfree.chart.renderer.xy.XYStepAreaRenderer;
import org.jfree.chart.renderer.xy.XYLineAndShapeRenderer;
import java.util.ArrayList;

public class Main {
    public static void main(String[] args) {
        XYStepAreaRenderer renderer = new XYStepAreaRenderer();
        XYLineAndShapeRenderer lineRenderer = new XYLineAndShapeRenderer();
        ArrayList<Double> stepPoints = new ArrayList<>();

        /* write */ renderer.setStepPoint(0.5);
        stepPoints.add(0.5);

        lineRenderer.setSeriesShapesVisible(0, true);
        lineRenderer.setSeriesLinesVisible(0, false);

        for (int i = 1; i <= 5; i++) {
            double newStepPoint = i * 0.1;
            renderer.setStepPoint(newStepPoint);
            stepPoints.add(newStepPoint);
        }

        lineRenderer.setSeriesShapesVisible(1, false);
        lineRenderer.setSeriesLinesVisible(1, true);

        double total = 0;
        for (Double point : stepPoints) {
            total += point;
        }
        double averageStepPoint = total / stepPoints.size();
        System.out.println("Average Step Point: " + averageStepPoint);

        /* read */ double stepPoint = renderer.getStepPoint();
        System.out.println("Current Step Point: " + stepPoint);

        lineRenderer.setSeriesShapesVisible(2, true);
        lineRenderer.setSeriesLinesVisible(2, true);

        for (int i = 0; i < stepPoints.size(); i++) {
            System.out.println("Step Point " + i + ": " + stepPoints.get(i));
        }
    }
}
